mapper.select:
SELECT COUNT(*)
FROM SEMIREVIEW
WHERE PNO=#{VALUE}

SELECT *
FROM SEMIPRODUCT
WHERE PNO= #{VALUE}

SELECT * FROM
	(SELECT ROWNUM RM, p.pno, p.cno, p.pname , p.pprice, p.ptype, p.pfilename, p.pstock
	FROM SEMIPRODUCT p, SEMIPREFER r
	WHERE p.pno = r.pno AND r.pfagegroup = #{VALUE}
	)
	WHERE RM = 1

mapper.update:
UPDATE SEMIQNA
SET QSTATE = 'false'
WHERE QNO= #{VALUE}

UPDATE SEMIEVENT
SET EPROGRESS = 'false'
WHERE ENO= #{VALUE}

mapper.select:
SELECT PASSWORD
FROM SEMIMEMBER
WHERE ID=#{VALUE}

SELECT PASSWORD
FROM SEMIMEMBER
WHERE ID=#{VALUE}

mapper.update:
UPDATE SEMIQNA
SET STEP = STEP+1
WHERE REF = #{ref} AND STEP>#{step}

UPDATE SEMIQNA
SET PNO = #{pno},
QTITLE = #{qtitle},
QFILENAME = #{qfilename},
QCONTENTS = #{qcontents}
WHERE QNO = #{qno}

		UPDATE SEMINOTICE
		SET NTITLE = #{ntitle},
		NCONTENTS = #{ncontents}
		WHERE NNO = #{nno}

		UPDATE SEMIEVENT
		SET ETITLE = #{etitle},
		EFILENAME = #{efilename},
		ECONTENTS = #{econtents}
		WHERE ENO = #{eno}

mapper.select:
SELECT c.CNAME
FROM SEMIPRODUCT p, SEMICOMPANY c
WHERE p.cno = c.cno AND p.pno = #{VALUE}

SELECT PNAME
FROM SEMIPRODUCT
WHERE PNO = #{VALUE}

SELECT *
FROM SEMIQNA
WHERE QNO=#{VALUE}

SELECT *
FROM SEMINOTICE
WHERE NNO=#{VALUE}

SELECT *
FROM SEMIEVENT
WHERE ENO=#{VALUE}

		SELECT CNO
		FROM SEMIPRODUCT
		WHERE PNO=#{VALUE}

SELECT ID, NAME,	NICKNAME,	PASSWORD,	GENDER,	TEL, BIRTHDATE,	EMAIL,	ADDRESS
FROM SEMIMEMBER
WHERE ID=#{id}

SELECT RNO,ID,CNO,PNO,RCOUNT,RDATE
FROM SEMIRESERVE
WHERE ID=#{id}

SELECT PNO,CNO,PNAME,PPRICE,PTYPE,PFILENAME
FROM SEMIPRODUCT
WHERE PNO=#{pno}

SELECT CNO,CNAME,CTEL,CADD
FROM SEMICOMPANY
WHERE CNO=#{cno}

SELECT PPRICE 
FROM SEMIPRODUCT
WHERE PNAME=#{VALUE}

SELECT CNAME 
FROM SEMICOMPANY
WHERE CNO=#{VALUE}

SELECT ID,CNAME,PNAME,RCOUNT,PPRICE,RDATE
FROM SMMEMBER
WHERE ID=#{VALUE}

SELECT SUM(PPRICE)
FROM SMMEMBER
WHERE ID=#{VALUE}

mapper.update:
